mapper.insert:
	INSERT INTO CATEGORY (
	         MEMBER_NO        /* 회원번호(FK) */
	       , CATEGORY_NO      /* 카테고리번호(PK) */
	       , CATEGORY_NM      /* 카테고리이름 */
	       , COLOR_CODE       /* 컬러코드(FK) */
	       )
	VALUES
	       (
	         #{member_no}
	       , (
	            SELECT NVL(MAX(CATEGORY_NO), 0) + 1
	              FROM CATEGORY
	             WHERE MEMBER_NO = #{member_no}
	         )
	       , #{category_nm}
	       , #{color_code}
    )
