mapper.insert:
INSERT INTO MEMBER (
			         MEMBER_NO      /* 회원번호(PK) */
			       , ID 			/* 아이디 */
			       , PASSWORD		/* 패스워드 */
			       , NAME			/* 이름 */
			       , EMAIL			/* 이메일 */
			       , JOIN_DATE		/* 가입일시 */
			       , MEMBERSHIP		/* 멤버십 */
		  	 	   )
		  	values (
		  			 (
			            SELECT NVL(MAX(MEMBER_NO), 0) + 1
			              FROM MEMBER
			         )
		  		   , #{id}
		  		   , #{password}
		  		   , #{name}
		  		   , #{email}
		  		   , SYSDATE
		  		   , #{membership}
		  		   )
