mapper.insert:
INSERT INTO MEMBER(
			MEMBER_NO
			, ID
			, PASSWORD
			, NAME
			, EMAIL
			, JOIN_DATE
			, MEMBERSHIP
			, DELAY_AUTO
			)
VALUES (
			(
				SELECT NVL(MAX(MEMBER_NO), 0) + 1
				FROM MEMBER
			)
			, #{id}
			, #{password}
			, #{name}
			, #{email}
			, SYSDATE
			, 1
			, 'N'
		)

mapper.select:
SELECT *
  FROM MEMBER
WHERE ID = #{id}
  AND PASSWORD = #{password}

mapper.update:
UPDATE MEMBER
   SET PASSWORD = #{password}
   	   , DELAY_AUTO = #{dealy_auto}
 WHERE ID = #{id}

mapper.select:
SELECT *
  FROM MEMBER
 WHERE ID = #{id}
   AND PASSWORD = #{password}
