mapper.insert:
INSERT INTO SCHEDULE (
		 SCHEDULE_NO     /* 일정번호(PK) */
       , MEMBER_NO       /* 회원번호(FK) */
       , CATEGORY_NO     /* 카테고리번호(FK) */
       , CONTENT         /* 내용 */
       , PLAN_DATE       /* 날짜 */
       , DDAY            /* 디데이 */
       , COMPLETE        /* 완료Y/N */
	   )
VALUES
	   (
		 (
            SELECT NVL(MAX(SCHEDULE_NO), 0) + 1
              FROM SCHEDULE
         )
       , #{member_no}
       , #{category_no}
       , #{content}
       , #{plan_date}
       , #{dday}
       , #{complete}
)
